import React, { useState } from "react";

const TextArea = () => {
  const [text, setText] = useState("");
  const [textareaHeight, setTextareHeight] = useState("auto");
  const handleChange = (e) => {
    setTextareHeight("auto");
    setText(e.target.value);
  };
  return (
    <div>
      <div className="p-5">
        <textarea
          className="rounded-lg resize-none transition-all overflow-hidden w-full max-w-[500px] p-5 border border-gray-500 focus:border-blue-400"
          placeholder="Please input your text..."
          style={{
            height: textareaHeight,
          }}
          value={text}
          onChange={handleChange}
        ></textarea>
      </div>
    </div>
  );
};

export default TextArea;
