import React, { useState } from "react";

const TextArea = () => {
  const [text, setText] = useState("");
  const [textareaHeight, setTextareHeight] = useState("auto");
  return (
    <div>
      <div>
        <textarea
          className="rounded-lg m-5 resize-none transition-all overflow-hidden w-full max-w-[500px] p-5 border border-gray-500 focus:border-blue-400"
          placeholder="Please input your text..."
          style={{
            height: textareaHeight,
          }}
        ></textarea>
      </div>
    </div>
  );
};

export default TextArea;
