import React, { useRef, useState } from "react";
import axios from "axios";
import lodash from "lodash";
// https://hn.algolia.com/api/v1/search?query=react
const HackerNews = () => {
  const [Hits, setHits] = useState([]);
  const [query, setQuery] = useState("");
  const [loading, setLoading] = useState(true);
  const [errorMessage, setErrorMessage] = useState("");
  const handleFetchData = useRef({});
  const handleUpdateQuery = lodash.debounce((e) => {
    setQuery(e.target.value);
  }, 500);
  handleFetchData.current = async () => {
    setLoading(true);
    try {
      const response = await axios.get(
        `https://hn.algolia.com/api/v1/search?query=${query}`
      );
      setHits(response.data?.hits || []); // Optional chaining
      setLoading(false);
    } catch (error) {
      setLoading(false);
      setErrorMessage(`The error was happeing ${error}`);
    }
  };
  React.useEffect(() => {
    handleFetchData.current();
  }, [query]);
  return (
    <div className="bg-white mx-auto mt-5 p-5 rounded-lg shadow-md w-2/4">
      <input
        type="text"
        className="border border-gray-200 block w-full rounded-md p-5 mb-5 transition-all focus:border-blue-400"
        defaultValue={query}
        placeholder="Typing your keyword..."
        onChange={handleUpdateQuery}
      />
      {!loading && (
        <div
          className="loading w-4 p-4 rounded-full border-blue-600 border-4 border-r-4
        border-r-transparent animate-spin mx-auto my-10"
        ></div>
      )}
      {/* {!loading && errorMessage && <p>{errorMessage}</p>}
      {!loading &&
        Hits.length > 0 &&
        Hits.map((item, index) => <h3 key={item.title}>{item.title}</h3>)} */}
    </div>
  );
};

export default HackerNews;
