import React from "react";

const TextArea = () => {
  return (
    <div>
      <div>
        <textarea className="rounded-lg m-5 resize-none transition-all overflow-hidden w-full max-w-[500px] p-5 border border-gray-500 focus:border-blue-400"></textarea>
      </div>
    </div>
  );
};

export default TextArea;
