import { useEffect, useRef } from "react";

export default function useLinkNewTab() {
  const contentRef = useRef(null);
  useEffect(() => {
    if (contentRef) {
      const links = contentRef.current.querySelectorAll("a");
      links.length > 0 &&
        links.forEach((item) => item.setAttribute("target", "_blank"));
      console.log(links);
    }
  }, []);
  return {
    contentRef,
  };
}
