// import logo from "./logo.svg";
import "./App.css";
import { youtubeData } from "./YoutubeData";
// import { youtubeData } from "./Youtubedata";
// JSX: Javascript XML
// JS6
// Babel
/**
 * element = <div id="root">Hello world</div>: JSX
 * element = React.createElement('div', {id: 'root'}, 'Hello world')
 * function createElement(elementType, props, ...children)
 * elementType: 'div' 'p' 'span'
 * props: class (Trong react là className), id, src, alt
 * ...children (phần tử con):
 */
// curly bases {}

// Parent component
function App() {
  // const numbers = [1, 2, 3, 4, 5];
  // const double = numbers.map((item) => item * 2);
  // console.log(double);
  return (
    <div className="youtube-list">
      {youtubeData.map((item, index) => {
        let newClass = "";
        if (index === 1) {
          newClass = "abc";
        }
        {
          /* const newClass1 = index === 1 ? "abc" : ""; */
        }
        return (
          <YoutubeItem
            key={item.id}
            image={item.image}
            avatar={item.avatar || item.image}
            author={item.author}
            title={item.title}
            className={newClass}
          ></YoutubeItem>
        );
      })}
    </div>
  );
}

// Props: Properties
function YoutubeItem(props) {
  return (
    <div className={`youtube-item ${props.className}`}>
      <div
        className="youtube-image"
        // style={{
        //   height: "250px",
        // }}
      >
        <img
          src={props.image}
          alt=""
          // style={{
          //   display: "block",
          //   maxWidth: "100%",
          //   width: "100%",
          //   height: "100%",
          //   objectFit: "cover",
          // }}
        />
      </div>
      <div className="youtube-footer">
        <img src={props.avatar} alt="" className="youtube-avatar" />
        <div className="youtube-content">
          <h3 className="youtube-title">
            {props.title || "This is example of title"}
          </h3>
          <h4 className="youtube-author">{props.author || "Thiệp đẹp zai"}</h4>
        </div>
      </div>
    </div>
  );
}

//Rendering list

export default App;
