import React, { useEffect, useState } from "react";
import axios from "axios";
const getRandomPhotos = (page) => {
  return axios
    .get(`https://picsum.photos/v2/list?page=${page}&limit=8`)
    .then((response) => {
      return response.data;
    })
    .catch((error) => {
      console.log(error);
    });
};
const Photos = () => {
  const [randomPhotos, setRandomPhotos] = useState([]);
  const [nextPage, setNextPage] = useState(1);

  const handleClickMore = () => {
    getRandomPhotos(nextPage).then((images) => {
      const photos = [...randomPhotos, ...images];
      setRandomPhotos(photos);
      setNextPage(nextPage + 1);
    });
  };
  useEffect(() => {}, []);

  return (
    <div>
      <div className="grid grid-cols-4 gap-5 p-5">
        {randomPhotos.map((item, index) => (
          <div
            key={item.id}
            className="bg-white shadow-sm p-3 h-[200px] rounded-lg"
          >
            <img
              src={item.download_url}
              alt={item.author}
              className="w-full h-full object-cover"
            />
          </div>
        ))}{" "}
      </div>
      <div className="text-center">
        <button
          onClick={handleClickMore}
          className="inline-block rounded-lg px-8 py-4 bg-purple-600 text-white"
        >
          Load more
        </button>
      </div>
    </div>
  );
};

export default Photos;
