import React, { useEffect, useState } from "react";
import axios from "axios";
const getRandomPhotos = () => {
  return axios
    .get("https://picsum.photos/v2/list?limit=8")
    .then(function (response) {
      return response.data;
    })
    .catch(function (error) {
      console.log(error);
    });
};
const Photos = () => {
  const [randomPhotos, setRandomPhotos] = useState([]);

  const handleLoadMore = () => {
    getRandomPhotos().then((images) => {
      const morePhotos = [...randomPhotos, ...images];
      setRandomPhotos(morePhotos);
    });
  };
  useEffect(() => {
    handleLoadMore();
  }, []);
  return (
    <div>
      <div className="grid grid-cols-4 gap-5 p-3">
        {randomPhotos.map((item, index) => (
          <div
            key={item.id}
            className="h-[200px] rounded-lg shadow-lg p-3 bg-white"
          >
            <img
              src={item.download_url}
              alt={item.author}
              className="w-full h-full object-cover"
            />
          </div>
        ))}
      </div>
      <div className="text-center">
        <button
          className="px-8 py-4 inline-block rounded-lg bg-purple-600 text-white"
          onClick={handleLoadMore}
        >
          Load more
        </button>
      </div>
    </div>
  );
};

export default Photos;
