function Toggle() {
  // 1. enabling state: useState(initialize value)
  // 2. initialize state: useState(false)
  // 3. reading state:
  // 4. updaing state:
  //   const array = useState(false);
  //   console.log(array); //[false, function]
  //   console.log(array[0]);
