import React, { useRef, useState } from "react";

const TextAreaAutoResize = () => {
  const [text, setText] = useState("");
  const textareaRef = useRef(null);
  const [textareaHeigh, setTextareaHeigh] = useState("auto");
  return (
    <div className="p-5">
      <textarea
        className="w-full max-w-[400px] p-5 rounded-lg border border-gray-300 resize-none"
        placeholder="Please enter your content..."
        value={text}
        ref={textareaRef}
      ></textarea>
    </div>
  );
};

export default TextAreaAutoResize;
