import React, { useEffect } from "react";
import axios from "axios";
// https://api.themoviedb.org/3/movie/550?api_key=628e09f41a4b7a1f3b3c15de74faa337
// https://api.themoviedb.org/3/search/movie?api_key=628e09f41a4b7a1f3b3c15de74faa337&query=''
const MovieSearchApp = () => {
  useEffect(() => {
    async function fetchData() {
      const result = await axios.get(
        `https://api.themoviedb.org/3/search/movie?api_key=628e09f41a4b7a1f3b3c15de74faa337&query=''`
      );
      console.log(result.data);
    }
    fetchData();
  }, []);
  return (
    <div className="p-10">
      <div className="w-full max-w-[500px] mx-auto mb-20">
        <input
          type="text"
          className="w-full p-5 rounded-lg border border-blue-500 transition-all 
          shadow-[0px_0px_0px_3px_rgba(125,_106,_255,_0.2)]"
          placeholder="Search movie..."
        />
      </div>
      <div className="grid grid-cols-3 gap-10">
        <MovieItem></MovieItem>
        <MovieItem></MovieItem>
        <MovieItem></MovieItem>
      </div>
    </div>
  );
};

const MovieItem = () => {
  return (
    <div className="bg-white p-12 rounded-2xl shadow-sm">
      <div className="h-[297px]">
        <img
          src="https://images.unsplash.com/photo-1598899134739-24c46f58b8c0?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1156&q=80"
          alt=""
          className="w-full h-full object-cover rounded-lg"
        />
      </div>
      <div className="p-7">
        <h3 className="text-lg text-black font-semibold mb-4">
          Hotel Transylvania: Puppy!
        </h3>
        <p className="text-[#999] text-sm mb-6">
          The residents of Hotel Transylvania find their world turned
          upside-down when youngster Dennis gets a surprise monster-sized pet.
        </p>
      </div>
    </div>
  );
};

export default MovieSearchApp;
